import './App.css';
import Header from "./MyComponents/Header";
import { Todos } from "./MyComponents/Todos";
import Footer from "./MyComponents/Footer";
import React, { useState } from 'react';
import {AddTodo} from './MyComponents/AddTodo';


function App() {
  const onDelete = (todo) => {
    console.log("I am onDelete of todo", todo);
    // Deleting this way in react does not work rather we can say that dom will not get updated
    // let index = todos.indexOf(todo);
    // todos.splice(index, 1);  //ek element delete kar do  

    setTodos(todos.filter((e)=>{
      return e!==todo;
      }));
  }

  const addTodo = (title,description)=>{
    console.log("I'm adding this todo",title,description)
    if(todos.length==0){
      Sno = 0;
    }
    else{
      let Sno = todos[todos.length-1].Sno+1;
    }
    const myTodo = {
      Sno:Sno,
      title:title,
      description:description
    }
    setTodos([...todos,myTodo]);   // array banaya ...todos,mytodo hai jisme
    console.log(myTodo);
  }

  const [todos, setTodos] = useState([
    {
      Sno: 1,
      title: "Go to the market",
      description: "To buy iphone"
    },
    {
      Sno: 2,
      title: "Text to SM",
      description: "Just for love"
    },
    {
      Sno: 3,
      title: "Code",
      description: "sprinklr"
    }
  ])
  return (
    // We have to wrap the content in <></> tag we cannot write directly in JSX
    <>
      <Header title="My Todos List" searchBar={false} />
      <AddTodo addTodo={addTodo}/>
      <Todos todos={todos} onDelete={onDelete} />
      <Footer />
    </>
  );
}
// since this is default export so we have to write import the file name and if this is name export then we have to write { in bracis } For eg se Todo.js
export default App;
