import './App.css';
import Header from "./MyComponents/Header";
import { Todos } from "./MyComponents/Todos";
import Footer from "./MyComponents/Footer";
import React, { useState, useEffect } from 'react';
import { AddTodo } from './MyComponents/AddTodo';
import {About} from './MyComponents/About';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useRouteMatch,
  useParams
} from "react-router-dom";

function App() {
  let initTodo;
  if (localStorage.getItem("todos") === null) {
    initTodo = [];
  }
  else {
    initTodo = JSON.parse(localStorage.getItem("todos"));
  }
  const onDelete = (todo) => {
    console.log("I am onDelete of todo", todo);
    // Deleting this way in react does not work rather we can say that dom will not get updated
    // let index = todos.indexOf(todo);
    // todos.splice(index, 1);  //ek element delete kar do  

    setTodos(todos.filter((e) => {
      return e !== todo;
    }));
    localStorage.setItem("todos", JSON.stringify(todos));
  }

  const addTodo = (title, description) => {
    console.log("I'm adding this todo", title, description)
    let Sno;
    if (todos.length == 0) {
      Sno = 0;
    }
    else {
      Sno = todos[todos.length - 1].Sno + 1;
    }
    const myTodo = {
      Sno: Sno,
      title: title,
      description: description
    }
    setTodos([...todos, myTodo]);   // array banaya ...todos,mytodo hai jisme
    console.log(myTodo);
  }

  const [todos, setTodos] = useState(initTodo);
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos])
  return (
    // We have to wrap the content in <></> tag we cannot write directly in JSX
    <>
    {/* Router se alag alag path pe kya render karana hai ye decide kar sakte ho */}
      <Router>
        <Header title="My Todos List" searchBar={false} />
        <Switch>
          {/* exact word us ekarn apd raha hai because apn ne / ko /about ko ek jaisa treat karega /about ke slash ko padhke execute kardega  /  maan ke  */}
          <Route path="/" render={()=>{
            return(
            <>
            <AddTodo addTodo={addTodo} />
        <Todos todos={todos} onDelete={onDelete} />
            </>
            )
          }}>
          </Route>
          <Route path="/about">
            <About />
          </Route>
        </Switch>
        <Footer />
      </Router>
    </>
  );
}
// since this is default export so we have to write import the file name and if this is name export then we have to write { in bracis } For eg se Todo.js
export default App;
